
public class ex04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 1.정수형 변수 num에 457넣기
		int num = 457;
		String[] Abo = { "4", "5", "7"};
		// 2.백의 자리 숫자만 담은 정수형 변수 result만들기 [산술형만 쓰기]
		int result_ = num / 100;
		int result = 100 * result_;
		System.out.println(result);
		System.out.println(Abo[0]+ "00"); 
	}

}
